    public class Spring : DruidLocation
    {

        public List<Location.LightField> lilyFields = new();

        public Spring() { }

        public Spring(string Name)
            : base(Name)
        {

        }

        public override void checkForMusic(GameTime time)
        {
            if (Game1.random.NextDouble() < 0.001)
            {
                localSound(SpellHandle.sounds.batFlap.ToString());
            }
            /*
            if (Game1.random.NextDouble() < 0.001)
            {
                localSound(SpellHandle.sounds.batScreech.ToString());
            }*/

            base.checkForMusic(time);

            if (!Game1.IsMusicContextActive())
            {

                Game1.changeMusicTrack("Upper_Ambient", true);

            }

        }

        public override void draw(SpriteBatch b)
        {

            base.draw(b);

            if ((int)(Game1.currentGameTime.TotalGameTime.TotalSeconds % 6) > 3)
            {

                Vector2 where = new(1344 - (float)Game1.viewport.X, 896 - (float)Game1.viewport.Y);

                List<Vector2> eyes = new()
                {
                    //new Vector2(12,28),
                    //new Vector2(0,28),
                    //new Vector2(24,20),
                    //new Vector2(36,20),
                    //new Vector2(48,24),
                    //new Vector2(60,24),
                    new Vector2(20,20),
                    new Vector2(40,20),
                };

                foreach (Vector2 place in eyes)
                {

                    Vector2 eye = place + where;

                    b.Draw(Game1.staminaRect, new Vector2(eye.X, eye.Y), new Rectangle((int)eye.X, (int)eye.Y, 4, 4), new Color(0, 174, 239), 0f, Vector2.Zero, 1f, 0, (eye.Y / 10000f) + 990f);

                }

            }

            if (Mod.instance.save.restoration[LocationData.druid_spring_name] >= 5)
            {

                foreach (Location.LightField light in lilyFields)
                {

                    light.draw(b);

                }

            }

        }

        protected override void _updateAmbientLighting()
        {
            
            Game1.ambientLight = new(96, 96, 64);

        }

