        public override void updateWarps()
        {

            if (warpSets.Count > 0)
            {

                warps.Clear();

                foreach (WarpTile warpSet in warpSets)
                {

                    warps.Add(new Warp(warpSet.enterX, warpSet.enterY, warpSet.location, warpSet.exitX, warpSet.exitY, flipFarmer: false));

                }

                return;

            }

            warpSets.Add(new WarpTile(26, 34, "Mine", 17, 5));

            warpSets.Add(new WarpTile(27, 34, "Mine", 17, 5));

            warpSets.Add(new WarpTile(28, 34, "Mine", 17, 5));

            warpSets.Add(new WarpTile(29, 34, "Mine", 17, 5));

            warps.Add(new Warp(26, 34, "Mine", 17, 6, flipFarmer: false));

            warps.Add(new Warp(27, 34, "Mine", 17, 6, flipFarmer: false));

            warps.Add(new Warp(28, 34, "Mine", 17, 6, flipFarmer: false));

            warps.Add(new Warp(29, 34, "Mine", 17, 6, flipFarmer: false));

        }

